CREATE TABLE IF NOT EXISTS `classificacao` (
  `id_classificacao` mediumint(9) NOT NULL AUTO_INCREMENT,
  `grau` int(11) NOT NULL,
  `cor` varchar(50) COLLATE utf8_bin NOT NULL,
  `cor2` varchar(50) COLLATE utf8_bin NOT NULL,
  `nome` varchar(100) COLLATE utf8_bin NOT NULL,
  `descricao` varchar(500) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`id_classificacao`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=1 ;

